        //   <tr key={comment}>
        //      <td>{comment}</td>
        //      <td>{sentiment}</td>
        //   </tr>
        // </tbody>
        // </table>


      <div className="card-body">
        <h4 className="card-title">Basic Table</h4>
          <p className="card-description"> Add className <code>.table</code>
          </p>
          <div className="table-responsive">
            <table className="table">
              <thead>
                <tr>
                  <th>Profile</th>
                  <th>VatNo.</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{comment}</td>
                  <td>{sentiment}</td>
                </tr>
              </tbody>
            </table>
          </div>
      </div>




       )
    })
  }
  render() {
    const {url,limit} = this.state
    const {posts} = this.state
    return (
      <div className="card">
               <div className="card-body">
                <h4 className="card-title">YouTube Comment Sentiment Analysis</h4>
                <form className="forms-sample" onSubmit={this.submitHandler}>
                  <Form.Group className="row">
                    <label htmlFor="exampleInputUsername2" className="col-sm-3 col-form-label">Video ID</label>
                    <div className="col-sm-9">
                    <Form.Control type="text" className="form-control" id="exampleInputUsername2" placeholder="Video Id" name="url" value={url} onChange={this.changeHandler} />
                    </div>
                  </Form.Group>
                  <Form.Group className="row">
                    <label htmlFor="exampleInputEmail2" className="col-sm-3 col-form-label">Number of Comments</label>
                    <div className="col-sm-9">
                    <Form.Control type="text" className="form-control" id="exampleInputEmail2" placeholder="Number of Comments" name="limit" value={limit} onChange={this.changeHandler}/>
                    </div>
                  </Form.Group>
                  {/*<Form.Group className="row">
                    <label htmlFor="exampleInputMobile" className="col-sm-3 col-form-label">Mobile</label>
                    <div className="col-sm-9">
